/**
 * Generated by `createschema notification.Device 'deviceId:Text; pushToken?:Text; pushTransport?:Select:firebase,apple,huawei; owner?:Relationship:User:SET_NULL; meta?:Json'`
 */

import { pick, get } from 'lodash'

import { Device, DeviceUpdateInput, QueryAllDevicesArgs } from '@app/condo/schema'

import { getClientSideSenderInfo } from '@condo/domains/common/utils/userid.utils'
import { generateReactHooks } from '@condo/domains/common/utils/codegeneration/generate.hooks'

import { Device as DeviceGQL } from '@condo/domains/notification/gql'

const FIELDS = ['id', 'deletedAt', 'createdAt', 'updatedAt', 'createdBy', 'updatedBy', 'deviceId', 'pushToken', 'pushTransport', 'owner', 'meta']
const RELATIONS = ['owner']

export interface IDeviceUIState extends Device {
    id: string
}

function convertToUIState (item: Device): IDeviceUIState {
    if (item.dv !== 1) throw new Error('unsupported item.dv')
    return pick(item, FIELDS) as IDeviceUIState
}

export interface IDeviceFormState {
    id?: undefined
}

function convertToUIFormState (state: IDeviceUIState): IDeviceFormState | undefined {
    if (!state) return
    const result = {}
    for (const attr of Object.keys(state)) {
        const attrId = get(state[attr], 'id')
        result[attr] = (RELATIONS.includes(attr) && state[attr]) ? attrId || state[attr] : state[attr]
    }
    return result as IDeviceFormState
}

function convertToGQLInput (state: IDeviceFormState): DeviceUpdateInput {
    const sender = getClientSideSenderInfo()
    const result = { dv: 1, sender }
    for (const attr of Object.keys(state)) {
        const attrId = get(state[attr], 'id')
        result[attr] = (RELATIONS.includes(attr) && state[attr]) ? { connect: { id: (attrId || state[attr]) } } : state[attr]
    }
    return result
}

const {
    useObject,
    useObjects,
    useCreate,
    useUpdate,
    useDelete,
} = generateReactHooks<Device, DeviceUpdateInput, IDeviceFormState, IDeviceUIState, QueryAllDevicesArgs>(DeviceGQL, { convertToGQLInput, convertToUIState })

export {
    useObject,
    useObjects,
    useCreate,
    useUpdate,
    useDelete,
    convertToUIFormState,
}
